import React, { useState, useEffect, useRef } from "react";
import {
  Animated,
  View,
  StyleSheet,
  ImageBackground,
  TouchableOpacity,
  SafeAreaView,
} from "react-native";
import AppLoading from "../components/AppLoading";
import { LinearGradient } from "expo-linear-gradient";
import Styles from "../config/Styles";
import Strings from "../config/Strings";
import ColorsApp from "../config/ColorsApp";
import { Title, Text, Divider, FAB } from "react-native-paper";
import SchedulePart from "../components/Home/SchedulePart";
import { DotIndicator } from "react-native-indicators";

export default function Home(props) {
  console.log("home");

  const yOffset = useRef(new Animated.Value(0)).current;
  const headerOpacity = yOffset.interpolate({
    inputRange: [0, 200],
    outputRange: [0, 1],
    extrapolate: "clamp",
  });

  const [isPageLoading, setIsPageLoaded] = useState(false);

  useEffect(() => {
    setIsPageLoaded(true);
  }, []);

  const onChangeScreen = (screen) => {
    props.navigation.navigate(screen);
  };

  useEffect(() => {
    props.navigation.setOptions({
      title: Strings.ST1,
      headerTransparent: true,
      headerTintColor: "#ffffff",
      headerTitleStyle: { opacity: headerOpacity },
      headerBackground: () => (
        <Animated.View
          style={{
            ...StyleSheet.absoluteFillObject,
            opacity: headerOpacity,
            backgroundColor: ColorsApp.PRIMARY,
          }}
        />
      ),
    });
  }, [headerOpacity]);

  if (isPageLoading) {
    return (
      <Animated.ScrollView
        onScroll={Animated.event(
          [
            {
              nativeEvent: {
                contentOffset: {
                  y: yOffset,
                },
              },
            },
          ],
          { useNativeDriver: true }
        )}
        scrollEventThrottle={16}
        showsHorizontalScrollIndicator={false}
        showsVerticalScrollIndicator={false}
      >
        <LinearGradient
          colors={["rgba(0,0,0,0.8)", "transparent"]}
          style={Styles.homeOverlay}
        />

        <ImageBackground
          source={require("../assets/images/header.jpg")}
          resizeMode={"cover"}
          style={Styles.headerBackground}
        >
          <View style={Styles.headerOverlay}>
            <Title style={Styles.headerTitle}>SMART PET FEEDER</Title>
            <TouchableOpacity
              onPress={() => onChangeScreen("Video")}
              activeOpacity={0.9}
            >
              <View style={Styles.headerButton}>
                <Text style={Styles.headerButtonText}>Watch Pet</Text>
              </View>
            </TouchableOpacity>
          </View>
        </ImageBackground>

        <SafeAreaView>
          <View style={Styles.HomeScreen}>
            <SchedulePart />
          </View>
        </SafeAreaView>
      </Animated.ScrollView>
    );
  } else {
    return <DotIndicator color={ColorsApp.PRIMARY} />;
  }
}
